import React from "react";
import { Container, Row, Col, ListGroup, ListGroupItem } from "react-bootstrap";
import { StarFill } from "react-bootstrap-icons";
import { useDispatch, useSelector } from "react-redux";
import { removeFromFav } from "../store/actions";

const mapDispatchToProps = (dispatch) => ({
  removeFromFav: (f) => {
    dispatch(removeFromFav(f));
  },
});



const Favourites = () => {

    const favsJobs = useSelector((state) => state.favourites.elements)
    return (
      <Container>
        <Row>
          <Col xs={12}>
            <ListGroup>
              {favsJobs.map((f) => (
                <ListGroupItem>
                  <StarFill onClick={() => this.props.removeFromFav(f)} />
                  <span>{f}</span>
                </ListGroupItem>
              ))}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    );
  }


export default connect((s) => s, mapDispatchToProps)(Favourites);
